css:
			/*样式开关*/

/*"display:"后添加"none"(不含引号)为关闭显示*/

.word {display:}		/*显示正面单词*/
.symbol {display:}		/*显示正面音标*/
.audio1 {display:}		/*显示正面单词发音按钮*/

.word1 {display:}		/*显示背面单词*/
.symbol1 {display:}		/*显示背面音标*/
.audio2 {display:}		/*显示背面单词发音按钮*/
.chn {display:}			/*显示汉语解释*/
.extra7 {display:}		/*显示个人笔记区域*/
.photo {display:}		/*显示图片*/
.phosen {display:}		/*显示图片例句*/
.ssound1 {display:}		/*显示图片例句的播放器图标*/
.photo2 {display:}		/*显示分类图片*/



/*正面及背面整体*/

.card{
 font-family:Helvetica,Sans-serif,Microsoft Yahei;
 font-size:16px;
 text-align:center;
 color:#222;
 line-height:150%;
 background-color:#eff0f4;}
.card.night_mode{background:#000000;color:#fff;}

.section1{
 border-radius:13px;
 background-color: #fff;
 position: relative;
 padding:5px;
 margin: 10px 0;
 box-shadow: 0 1px 1px 0px rgba(0, 0, 0, 0.4),0 0px 0px 1px rgba(0, 0, 0, 0.02);}
.nightMode .section1{background-color: #1c1c1e;}

.section2{
 border-radius:13px;
 background-color: #f8fdf8;
 position: relative;
 padding:5px;
 margin: 10px 0;
 box-shadow: 0 1px 1px 0px rgba(0, 0, 0, 0.4),0 0px 0px 1px rgba(0, 0, 0, 0.02);}
.nightMode .section2{background-color: #1c1c1e;}

.section3{
 border-radius:13px;
 background-color: #fff;
 position: relative;
 padding:5px;
 margin: 10px 0;
 box-shadow: 0 1px 1px 0px rgba(0, 0, 0, 0.4),0 0px 0px 1px rgba(0, 0, 0, 0.02);}
.nightMode .section3{background-color: #1c1c1e;}

.unit{margin-top:5px;
 border:0px;
 border-radius: 5px; 
 padding: 3px 20px;
 background-color: #99cc00;
 font-size: 15px;
 color:#fff;
 display:inline-block;
 line-height: 120%;}
.nightMode .unit{ background-color: darkgray;}

.word{
 padding-top:5px;
 font-size:35px;
 color:#ff6600;
 line-height:120%;}
.nightMode .word{color:#fff;}

.word1{
 padding-top:5px;
 font-size:35px;
 color:#ff6600;
 line-height:120%;}
.nightMode .word1{color:#fff;}

.word2{
 padding-top:5px;
 font-size:30px;
 color:#ff6600;
 line-height:100%;}
.nightMode .word2{color:#fff;}

.symbol{
 font-family:Georgia;
 font-size: 16px; 
 color:#3d7fff;}
.nightMode .symbol{color:pink;}

.symbol1{
 font-family:Georgia;
 font-size: 16px; 
 color:#3d7fff;}
.nightMode .symbol1{color:pink;}

.audio1{padding-top:5px;}
.audio2{padding-top:5px;}


/*中文*/
.chn{padding:10px 0;
 font-size: 25px;
 font-family:Microsoft Yahei;
 line-height:150%;}
.nightMode .chn {color:#fff;}


/*个人笔记*/
.extra7 {color:#CC0033;
 font-size: 20px;}
.nightMode .extra7 {color:#00e8ee;}


/*图片*/
.photo {height: auto;
 padding-top:5px;}
.photo img{
 -webkit-filter: drop-shadow(1px 1px 1px rgba(0,0,0,0.3));
 filter:drop-shadow(1px 1px 1px rgba(0,0,0,0.3));
 border-radius: 10px;
 -webkit-border-radius: 10px;
 -moz-border-radius: 10px;}


/*图片的例句*/
.phosen{color:#4e4d4d;
 font-size:18px;
 padding-top:5px;}
.nightMode .phosen {color:#eaf47f;}
.senen2 {color:red;}
.nightMode .senen2 {color:yellow;}
.sencn2 {color:red;}
.nightMode .sencn2 {color:yellow;}

/*例句发音播放*/
.ssound1{ padding-top:5px;}


/*分类图片*/
.photo2 {
 border-radius:13px;
 background-color: #fffdff;
 position: relative;
 padding:5px;
 margin: 10px 0;
 box-shadow: 0 1px 1px 0px rgba(0, 0, 0, 0.4),0 0px 0px 1px rgba(0, 0, 0, 0.02);}
.nightMode .photo2{background-color: #1c1c1e;}
.nopd{padding: 5px 5px;}
.nightMode .nopd{color:#fafafa;
 background-color:#1c1c1e;}
.nopd img{
 -webkit-filter: drop-shadow(1px 1px 1px rgba(0,0,0,0.3));
 filter:drop-shadow(1px 1px 1px rgba(0,0,0,0.3));
 border-radius: 10px;
 -webkit-border-radius: 10px;
 -moz-border-radius: 10px;}
span.nopline{font-weight:bold;}
span.nop1{color:#b22222;}
.nightMode .nop1{color:pink;}


/*播放器图标样式*/
.replay-button svg {
  width: 25px;
  height: 25px;}
.replay-button svg circle {
  fill:#231F20;
  stroke:#ffffff;}   /*外圆*/
.nightMode .replay-button svg circle {
  fill:#dce0df;
  stroke:#000000;} 
.replay-button svg path {
  stroke:#91C462;
  fill: #91C462;}   /*内三角*/
.nightMode .replay-button svg path {
  stroke:#4e4d4d;
  fill: #4e4d4d;}  

code#typeans {font-family: "Helvetica,Microsoft Yahei,Sans-serif"; }
#typeans { font-size: 25px !important; }
