css:
.card {
  font-family: Helvetica, Sans-serif, Microsoft Yahei;
  font-size: 16px;
  text-align: center;
  color: #222;
  line-height: 150%;
  background-color: #eff0f4;
}

.card.night_mode {
  background: #000000;
  color: #fff;
}

.section1 {
  border-radius: 13px;
  background-color: #fff;
  position: relative;
  padding: 5px;
  margin: 10px 0;
  box-shadow: 0 1px 1px 0px rgba(0, 0, 0, 0.4), 0 0px 0px 1px rgba(0, 0, 0, 0.02);
}

.nightMode .section1 {
  background-color: #1c1c1e;
}

.section2 {
  border-radius: 13px;
  background-color: #f8fdf8;
  position: relative;
  padding: 5px;
  margin: 10px 0;
  box-shadow: 0 1px 1px 0px rgba(0, 0, 0, 0.4), 0 0px 0px 1px rgba(0, 0, 0, 0.02);
}

.nightMode .section2 {
  background-color: #1c1c1e;
}

.section3 {
  border-radius: 13px;
  background-color: #fff;
  position: relative;
  padding: 5px;
  margin: 10px 0;
  box-shadow: 0 1px 1px 0px rgba(0, 0, 0, 0.4), 0 0px 0px 1px rgba(0, 0, 0, 0.02);
}

.nightMode .section3 {
  background-color: #1c1c1e;
}

.unit {
  margin-top: 5px;
  border: 0px;
  border-radius: 5px;
  padding: 3px 20px;
  background-color: #99cc00;
  font-size: 15px;
  color: #fff;
  display: inline-block;
  line-height: 120%;
}

.nightMode .unit {
  background-color: darkgray;
}

.word {
  padding-top: 5px;
  font-size: 35px;
  color: #ff6600;
  line-height: 120%;
}

.nightMode .word {
  color: #fff;
}

.photo {
  height: auto;
  padding-top: 5px;
}

.photo img {
  -webkit-filter: drop-shadow(1px 1px 1px rgba(0, 0, 0, 0.3));
  filter: drop-shadow(1px 1px 1px rgba(0, 0, 0, 0.3));
  border-radius: 10px;
}

.phosen {
  color: #4e4d4d;
  font-size: 18px;
  padding-top: 5px;
}

.nightMode .phosen {
  color: #eaf47f;
}
